import React from "react";
import axios from "axios";
import { Radio } from "bootstrap";
import "./App.css";
import { Panel, Well, Fade, Collapse, Button } from "react-bootstrap";
// import "bootstrap/dist/css/bootstrap.css";

export default class Vote extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: "",
      open: false,
      questionList: [
        {
          id: 1,
          content: "Will Nox bring us a house ?",
          hasVoted: false,
          totalYes: 35,
          totalNo: 14,
          voteFor: "Yes",
          openStat: false,
          owner: "geoff"
        },
        {
          id: 2,
          content: "Will LX bring us a house ?",
          hasVoted: true,
          totalYes: 5,
          totalNo: 4,
          voteFor: "Yes",
          openStat: false,
          owner: "michal"
        },
        {
          id: 3,
          content: "Will Nox bring us a house ?",
          hasVoted: false,
          totalYes: 115,
          totalNo: 14,
          voteFor: "Yes",
          openStat: false,
          owner: "fa"
        },
        {
          id: 4,
          content: "Will Nox bring us a house ?",
          hasVoted: true,
          totalYes: 25,
          totalNo: 44,
          voteFor: "Yes",
          openStat: false,
          owner: "edd"
        },
        {
          id: 5,
          content: "Will Nox bring us a house ?",
          hasVoted: false,
          totalYes: 65,
          totalNo: 34,
          voteFor: "Yes",
          openStat: false,
          owner: "geoff"
        }
      ],
      currentId: 6,
      showVoteResult: false
    };
  }

  /*     componentWillMount(){
            // need to get the data here
            axios.get('https://jr-001-pawpatrol-course-api.herokuapp.com/api/courses')
                .then((res)=>{
                    console.log(res);
                    this.setState(()=>{
                      return({courses:res.data})
                    });
                })
                .catch((error)=>{throw(error)});
            } */

  handleSubmit = e => {
    e.preventDefault();
    console.log(this.state);
    this.setState({
      questionList: [
        ...this.state.questionList,
        {
          id: this.state.currentId,
          content: this.myinput.value,
          hasVoted: false,
          totalYes: 315,
          totalNo: 14,
          voteFor: "Yes",
          openStat: false,
          owner: "geoff"
        }
      ],
      currentId: this.state.currentId + 1
    });
  };

  handleCollapse = (item, index, str, e) => {
    console.log(index);
    console.log(str);
    // console.log(this.state.questionList[index].openStat);
    if (item.hasVoted == false) {
      const questionList = this.state.questionList;
      item.openStat = !item.openStat;
      // this.state.questionList[index].openStat = !this.state.questionList[index]
      //   .openStat;
      item.hasVoted = true;
      // let ty = this.state.questionList[index].totalYes;
      // let tn = this.state.questionList[index].totalNo;
      str === "yes" ? ++item.totalYes : ++item.totalNo;
      // console.log(ty, tn);
      this.forceUpdate();
      this.setState(questionList);
    } else {
      item.openStat = !item.openStat;
      this.forceUpdate();
    }
  };

  handeleDelete = id => {
    id++;
    console.log("del");
    const questionList = this.state.questionList.filter(q => q.id !== id);
    console.log(questionList.length);
    this.forceUpdate();
    this.setState({ questionList: questionList });
  };

  handleVote = vid => {
    console.log("here");
    console.log(vid.totalNo);
    // this.props.totalNo++;
    // console.log(this, this.pirops.totalNo);
    // this.setState({
    //   showVoteResult: !this.state.showVoteResult
    // });
  };
  render() {
    // let totalYes = 0
    // let totalNo = 0
    // this.state.questionList.forEach((item) => {
    //     item.Status ? totalYes++ : totalNo++
    // })

    return (
      <div className="jumbotron mx-auto appFrame">
        <h1 className="display-4 text-center" style={{ margin: "0 auto" }}>
          Vote SubSystem
        </h1>
        {/* <Button onClick={() => this.setState({ open: !this.state.open })}>
          Click to toggle
        </Button>
        <br />
        <Collapse in={this.state.open}>
          <div>
            <Well>
              Anim pariatur cliche reprehenderit, enim eiusmod high life
              accusamus terry richardson ad squid. Nihil anim keffiyeh
              helvetica, craft beer labore wes anderson cred nesciunt sapiente
              ea proident.
            </Well>
          </div>
        </Collapse> */}

        <form onSubmit={this.handleSubmit} className="centerBlock">
          <input
            type="text"
            placeholder="New Question"
            className="form-control"
            ref={a => {
              this.myinput = a;
            }}
            onChange={e => {
              console.log("onchange", e.target.value);
              this.setState({
                inputValue: e.target.value
              });
            }}
          />
          <button
            type="submit"
            className="btn btn-primary m-3 mx-auto"
            onClick={this.handleSubmit}
          >
            Create New Question
          </button>

          <div className="card-body">
            <div className="table-responsive">
              <table className="table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Question</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {this.state.questionList.map((item, index) => {
                    const hasVoted = item.hasVoted;
                    const isOnwer = item.owner == "geoff" ? true : false;
                    return (
                      <tr key={index}>
                        <div>
                          <td scope="row">{item.id}</td>
                          <td>{item.content}</td>
                          {hasVoted ? (
                            <td>
                              <span
                                className="btn btn-danger m-2"
                                onClick={
                                  e =>
                                    this.handleCollapse(item, index, "check", e) // this.setState({ open: !this.state.open })
                                }
                              >
                                Check Results
                              </span>
                            </td>
                          ) : (
                            <td>
                              <span
                                className="btn btn-success m-2"
                                onClick={
                                  e =>
                                    this.handleCollapse(item, index, "yes", e) // this.setState({ open: !this.state.open })
                                }
                              >
                                Yes
                              </span>
                              <span className="btn btn-danger m-2">No</span>
                            </td>
                          )}
                          {isOnwer ? (
                            <td>
                              <span
                                className="btn btn-danger m-2"
                                onClick={() => this.handeleDelete(index)}
                              >
                                Delete
                              </span>
                            </td>
                          ) : (
                            ""
                          )}
                          <Collapse className="collaps" in={item.openStat}>
                            <div>
                              <Well>
                                Total Yes: {item.totalYes} vs Total No:{" "}
                                {item.totalNo}
                              </Well>
                            </div>
                          </Collapse>
                        </div>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>

          {/* <ul className="list-group">
            {this.state.questionList.map((item, index) => {
              let groupName = `group + ${index}`;
              return (
                <li className="list-group-item" key={index + Date()}>
                  <span
                    style={{ float: "left" }}
                    className="badge badge-secondary m-1"
                  >
                    {" "}
                    {item.id}{" "}
                  </span>
                  <span className="ml-1">{item.content}</span>
                  <label className="ml-5 form-check-label">
                    <input
                      type="radio"
                      className="form-check-input"
                      defaultChecked={item.Status}
                      name={groupName}
                    />YES
                  </label>
                  <label className="ml-5 form-check-label">
                    <input
                      type="radio"
                      className="form-check-input"
                      onChange={() => this.handleVote(item)}
                      name={groupName}
                    />NO
                  </label>
                </li>
              );
            })}
          </ul> */}
        </form>
        <div className="text-center mx-auto">
          <button className="btn btn-primary m-1" onClick={this.handleVote}>
            Vote Results
          </button>
          <div
            className={
              this.state.showVoteResult ? "voteResultDisplay" : "voteResultHide"
            }
          >
            {/* Vote result, yes number : {totalYes} , no number: {totalNo} */}
          </div>
        </div>
      </div>
    );
  }
}
